import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Certifique-se de que você tem o React Router configurado

const Usuario: React.FC = () => {
  const [comment, setComment] = useState('');

  const navigate = useNavigate();

  const handleCommentChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setComment(event.target.value);
  };

  const handleEdit = () => {
    // Lógica para editar o comentário
  };

  const handleDelete = () => {
    // Lógica para excluir o comentário
  };

  const handlePostComment = () => {
    // Lógica para postar o comentário
    console.log('Comentário lançado:', comment);
    // Redirecionar para a página de visualização de posts
    navigate.push('/visualizar-posts');
  };

  const handleViewPosts = () => {
    // Redirecionar para a página de visualização de posts
    navigate.push('/visualizar-posts');
  };

  return (
    <div>
      <textarea
        placeholder="Insira sua dúvida ou solução"
        value={comment}
        onChange={handleCommentChange}
      />
      <button onClick={handleEdit}>Editar</button>
      <button onClick={handleDelete}>Excluir</button>
      <button onClick={handlePostComment}>Lançar Comentário</button>
      <button onClick={handleViewPosts}>Visualizar Posts</button>
    </div>
  );
};

export default Usuario;
