import React, { useState } from 'react';
import { useCommentContext } from '../../components/comments/CommentContext';
import { useNavigate } from 'react-router-dom';
import './styles.css';

const Usuario: React.FC = () => {
  const [comment, setComment] = useState('');
  const { comments, addComment } = useCommentContext();
  const navigate = useNavigate();

  const [editIndex, setEditIndex] = useState<number | null>(null); // Adicione esta linha

  // Função para excluir um comentário
  const handleDeleteComment = (index: number) => {
    const newComments = comments.filter((_, i) => i !== index);
    // Atualiza a lista de comentários
    newComments.forEach(comment => addComment(comment));  // Adiciona cada comentário individualmente
  };
  
  

  // Função para editar um comentário
  const handleEditComment = (index: number) => {
    const commentToEdit = comments[index];
    setComment(commentToEdit);
    // Você pode implementar a funcionalidade de edição aqui se necessário
  };

  const handleCommentChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setComment(event.target.value);
  };

  const handlePostComment = () => {
    if (comment.trim() !== '') {
      if (editIndex !== null) {
        const updatedComments = [...comments];
        updatedComments[editIndex] = comment;
        addComment(comment);  // Adiciona um único comentário
        setComment('');
        setEditIndex(null);
      } else {
        // Adiciona um novo comentário ao array de comentários existente
        addComment(comment);
        setComment('');
      }
    }
  };
  
  
  
  

  const handleViewPosts = () => {
    navigate('/principal');
  };

  return (
    <div className="usuario-page">
      <textarea
        placeholder="Insira sua dúvida ou solução."
        value={comment}
        onChange={handleCommentChange}
      />
      <button onClick={handlePostComment}>
        {editIndex !== null ? 'Salvar Edição' : 'Lançar Comentário'}
      </button>
      <button onClick={handleViewPosts}>Visualizar Posts</button>

      <div className="comments-list">
        <h2>Comentários:</h2>
        <ul>
          {comments.map((comment, index) => (
            <li key={index}>
              <div className="comment-item">
                <span>{comment}</span>
                <button onClick={() => handleDeleteComment(index)}>Excluir</button>
                <button onClick={() => handleEditComment(index)}>Editar</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Usuario;
