import React, { createContext, useState, ReactNode } from 'react';

interface CommentContextProps {
  comments: string[];
  addComment: (comment: string) => void;
}

interface CommentProviderProps {
  children: ReactNode;
}

const CommentContext = createContext<CommentContextProps | undefined>(undefined);

export const CommentProvider: React.FC = ({ children }) => {
  const [comments, setComments] = useState<string[]>([]);

  const addComment = (newComments: string[]) => {  // Modificado para aceitar um array de strings
    setComments(newComments);
  };

  return (
    <CommentContext.Provider value={{ comments, addComment }}>
      {children}
    </CommentContext.Provider>
  );
};

export const useCommentContext = () => {
  const context = React.useContext(CommentContext);
  if (!context) {
    throw new Error('useCommentContext must be used within a CommentProvider');
  }
  return context;
};
