import React, { useState } from 'react';
import { useCommentContext } from '../../components/comments/CommentContext';
import { useNavigate } from 'react-router-dom';
import './styles.css';

const Usuario: React.FC = () => {
  const [comment, setComment] = useState('');
  const { comments, addComment } = useCommentContext();
  const navigate = useNavigate();

  const handleCommentChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setComment(event.target.value);
  };

  const handlePostComment = () => {
    if (comment.trim() !== '') {
      addComment(comment);
      setComment('');
    }
  };

  const handleViewPosts = () => {
    navigate('/principal');
  };

  const handleDeleteComment = (index: number) => {
    const newComments = [...comments];
    newComments.splice(index, 1);
    // A função addComment atualiza os comentários via contexto
    addComment(newComments);
  };


  const handleEditComment = (index: number) => {
    // Função para editar um comentário pelo índice
    const commentToEdit = comments[index];
    setComment(commentToEdit);
    // Ação de editar comentário aqui...
  };

  return (
    <div className="usuario-page">
      <textarea
        placeholder="Insira sua dúvida ou solução."
        value={comment}
        onChange={handleCommentChange}
      />
      <button onClick={handlePostComment}>Lançar Comentário</button>
      <button onClick={handleViewPosts}>Visualizar Posts</button>

      <div className="comments-list">
        <h2>Comentários:</h2>
        <ul>
          {comments.map((comment, index) => (
            <li key={index}>
              <div className="comment-item">
                <span>{comment}</span>
                <button onClick={() => handleEditComment(index)}>Editar</button>
                <button onClick={() => handleDeleteComment(index)}>Excluir</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Usuario;
