const Usuario: React.FC = () => {
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState<string[]>([]);
  const [editIndex, setEditIndex] = useState<number | null>(null);
  const navigate = useNavigate();

  const handleCommentChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setComment(event.target.value);
  };

  const handlePostComment = () => {
    if (comment.trim() !== '') {
      if (editIndex !== null) {
        const updatedComments = [...comments];
        updatedComments[editIndex] = comment;
        setComments(updatedComments);
        setEditIndex(null);
      } else {
        setComments([...comments, comment]);
      }
      setComment('');
    }
  };

  const handleDeleteComment = (index: number) => {
    const newComments = [...comments];
    newComments.splice(index, 1);
    setComments(newComments);
  };

  const handleEdit = (index: number) => {
    setComment(comments[index]);
    setEditIndex(index);
  };

  const handleViewPosts = () => {
    navigate('/principal');
  };

  return (
    <div className="usuario-page"> {/* Adicionando a classe usuario-page */}
      <textarea
        placeholder="Insira sua dúvida ou solução."
        value={comment}
        onChange={handleCommentChange}
      />
      <button onClick={handlePostComment}>
        {editIndex !== null ? 'Salvar Edição' : 'Lançar Comentário'}
      </button>
      {comments.map((comment, index: number) => (
  <div key={index}>
    <p>{comment}</p>
    <button onClick={() => handleDeleteComment(index)}>Excluir</button>
    <button onClick={() => handleEdit(index)}>Editar</button>
  </div>
))}

      <button onClick={handleViewPosts}>Visualizar Posts</button>
    </div>
  );
};

export default Usuario;
